document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelectorAll(".content-section"),t=new IntersectionObserver((e=>{e.forEach((e=>{e.isIntersecting?e.target.classList.add("section-visible"):e.target.classList.remove("section-visible")}))}));e.forEach((e=>{t.observe(e)})),document.querySelectorAll(".smooth-scroll").forEach((e=>{e.addEventListener("click",(function(e){e.preventDefault();const t=this.getAttribute("href").substring(1),n=document.getElementById(t);n&&n.scrollIntoView({behavior:"smooth"})}))}))}));